    // Number of operations to compute geometry constants: 385
    const double G0 = W1*det*(Jinv_01*Jinv_01*w[1][0] + w[0][0]*(2*Jinv_01*Jinv_01 + Jinv_00*Jinv_00 + Jinv_02*Jinv_02));
