    const double G1_0_1 = 1.0 / (detJ)*det*J_10*K_11*(1.0);
    const double G1_1_0 = 1.0 / (detJ)*det*J_11*K_01*(1.0);
    const double G1_1_1 = 1.0 / (detJ)*det*J_11*K_11*(1.0);
    const double G2_0_0 = 1.0 / (detJ)*det*J_00*K_00*(1.0);
