/// This class defines the interface for the tabulation of the cell
/// tensor corresponding to the local contribution to a form from
/// the integral over a cell.

class stokes_cell_integral_0_0: public ufc::cell_integral
{
public:

  /// Constructor
  stokes_cell_integral_0_0() : ufc::cell_integral()
  {
    // Do nothing
  }

  /// Destructor
  virtual ~stokes_cell_integral_0_0()
  {
    // Do nothing
  }

  /// Tabulate the tensor for the contribution from a local cell
  virtual void tabulate_tensor(double* A,
                               const double * const * w,
                               const ufc::cell& c) const
  {
    // Extract vertex coordinates
    const double * const * x = c.coordinates;
    
    // Compute Jacobian of affine map from reference cell
    const double J_00 = x[1][0] - x[0][0];
    const double J_01 = x[2][0] - x[0][0];
    const double J_10 = x[1][1] - x[0][1];
    const double J_11 = x[2][1] - x[0][1];
    
    // Compute determinant of Jacobian
    double detJ = J_00*J_11 - J_01*J_10;
    
    // Compute inverse of Jacobian
    const double K_00 =  J_11 / detJ;
    const double K_01 = -J_01 / detJ;
    const double K_10 = -J_10 / detJ;
    const double K_11 =  J_00 / detJ;
    
    // Set scale factor
    const double det = std::abs(detJ);
    
    // Cell Volume.
    const double volume = std::abs(detJ)/2.0;
    
    // Compute circumradius, assuming triangle is embedded in 2D.
    const double v1v2  = std::sqrt( (x[2][0] - x[1][0])*(x[2][0] - x[1][0]) + (x[2][1] - x[1][1])*(x[2][1] - x[1][1]) );
    const double v0v2  = std::sqrt( J_11*J_11 + J_01*J_01 );
    const double v0v1  = std::sqrt( J_00*J_00 + J_10*J_10 );
    
    const double circumradius = 0.25*(v1v2*v0v2*v0v1)/(volume);
    
    // Array of quadrature weights.
    static const double W1 = 0.500000000000000;
    // Quadrature points on the UFC reference element: (0.333333333333333, 0.333333333333333)
    
    // Value of basis functions at quadrature points.
    static const double FE0_C0[1][3] = \
    {{0.333333333333333, 0.333333333333333, 0.333333333333333}};
    
    // Array of non-zero columns
    static const unsigned int nzc6[3] = {6, 7, 8};
    
    static const double FE0_C0_D01[1][2] = \
    {{-1.000000000000000, 1.000000000000000}};
    
    // Array of non-zero columns
    static const unsigned int nzc4[2] = {3, 5};
    
    // Array of non-zero columns
    static const unsigned int nzc8[2] = {6, 7};
    
    // Array of non-zero columns
    static const unsigned int nzc5[2] = {3, 4};
    
    // Array of non-zero columns
    static const unsigned int nzc2[2] = {0, 1};
    
    // Array of non-zero columns
    static const unsigned int nzc1[2] = {0, 2};
    
    // Array of non-zero columns
    static const unsigned int nzc7[2] = {6, 8};
    
    // Reset values in the element tensor.
    for (unsigned int r = 0; r < 81; r++)
    {
      A[r] = 0.000000000000000;
    }// end loop over 'r'
    // Number of operations to compute geometry constants: 55.
    double G[14];
    G[0] = K_01*W1*det;
    G[1] = K_11*W1*det;
    G[2] = K_00*W1*det;
    G[3] = K_10*W1*det;
    G[4] = W1*det*(K_00*K_10 + K_01*K_11);
    G[5] = W1*det*(K_10*K_10 + K_11*K_11);
    G[6] = 0.800000000000000*W1*circumradius*circumradius*det*(K_00*K_10 + K_01*K_11);
    G[7] = W1*det*(K_00*K_00 + K_01*K_01);
    G[8] = 0.800000000000000*W1*circumradius*circumradius*det*(K_00*K_00 + K_01*K_01);
    G[9] = 0.800000000000000*W1*circumradius*circumradius*det*(K_10*K_10 + K_11*K_11);
    G[10] =  - K_10*W1*det;
    G[11] =  - K_11*W1*det;
    G[12] =  - K_01*W1*det;
    G[13] =  - K_00*W1*det;
    
    // Compute element tensor using UFL quadrature representation
    // Optimisations: ('eliminate zeros', True), ('ignore ones', True), ('ignore zero tables', True), ('optimisation', 'simplify_expressions'), ('remove zero terms', True)
    
    // Loop quadrature points for integral.
    // Number of operations to compute element tensor for following IP loop = 288
    // Only 1 integration point, omitting IP loop.
    
    // Number of operations for primary indices: 72
    for (unsigned int j = 0; j < 3; j++)
    {
      for (unsigned int k = 0; k < 2; k++)
      {
        // Number of operations to compute entry: 3
        A[nzc6[j]*9 + nzc5[k]] += FE0_C0[0][j]*FE0_C0_D01[0][k]*G[0];
        // Number of operations to compute entry: 3
        A[nzc6[j]*9 + nzc4[k]] += FE0_C0[0][j]*FE0_C0_D01[0][k]*G[1];
        // Number of operations to compute entry: 3
        A[nzc6[j]*9 + nzc2[k]] += FE0_C0[0][j]*FE0_C0_D01[0][k]*G[2];
        // Number of operations to compute entry: 3
        A[nzc6[j]*9 + nzc1[k]] += FE0_C0[0][j]*FE0_C0_D01[0][k]*G[3];
      }// end loop over 'k'
    }// end loop over 'j'
    
    // Number of operations for primary indices: 144
    for (unsigned int j = 0; j < 2; j++)
    {
      for (unsigned int k = 0; k < 2; k++)
      {
        // Number of operations to compute entry: 3
        A[nzc2[j]*9 + nzc1[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[4];
        // Number of operations to compute entry: 3
        A[nzc1[j]*9 + nzc1[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[5];
        // Number of operations to compute entry: 3
        A[nzc1[j]*9 + nzc2[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[4];
        // Number of operations to compute entry: 3
        A[nzc5[j]*9 + nzc4[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[4];
        // Number of operations to compute entry: 3
        A[nzc4[j]*9 + nzc4[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[5];
        // Number of operations to compute entry: 3
        A[nzc8[j]*9 + nzc7[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[6];
        // Number of operations to compute entry: 3
        A[nzc4[j]*9 + nzc5[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[4];
        // Number of operations to compute entry: 3
        A[nzc2[j]*9 + nzc2[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[7];
        // Number of operations to compute entry: 3
        A[nzc5[j]*9 + nzc5[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[7];
        // Number of operations to compute entry: 3
        A[nzc8[j]*9 + nzc8[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[8];
        // Number of operations to compute entry: 3
        A[nzc7[j]*9 + nzc8[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[6];
        // Number of operations to compute entry: 3
        A[nzc7[j]*9 + nzc7[k]] += FE0_C0_D01[0][j]*FE0_C0_D01[0][k]*G[9];
      }// end loop over 'k'
    }// end loop over 'j'
    
    // Number of operations for primary indices: 72
    for (unsigned int j = 0; j < 2; j++)
    {
      for (unsigned int k = 0; k < 3; k++)
      {
        // Number of operations to compute entry: 3
        A[nzc1[j]*9 + nzc6[k]] += FE0_C0[0][k]*FE0_C0_D01[0][j]*G[10];
        // Number of operations to compute entry: 3
        A[nzc4[j]*9 + nzc6[k]] += FE0_C0[0][k]*FE0_C0_D01[0][j]*G[11];
        // Number of operations to compute entry: 3
        A[nzc5[j]*9 + nzc6[k]] += FE0_C0[0][k]*FE0_C0_D01[0][j]*G[12];
        // Number of operations to compute entry: 3
        A[nzc2[j]*9 + nzc6[k]] += FE0_C0[0][k]*FE0_C0_D01[0][j]*G[13];
      }// end loop over 'k'
    }// end loop over 'j'
  }

  /// Tabulate the tensor for the contribution from a local cell
  /// using the specified reference cell quadrature points/weights
  virtual void tabulate_tensor(double* A,
                               const double * const * w,
                               const ufc::cell& c,
                               unsigned int num_quadrature_points,
                               const double * const * quadrature_points,
                               const double* quadrature_weights) const
  {
    throw std::runtime_error("Quadrature version of tabulate_tensor not yet implemented (introduced in UFC 2.0).");
  }

};

