    const double G1_0_1_0 = det*c0_1_0_0*Jinv_10;
    const double G1_1_0_0 = det*c0_1_0_1*Jinv_00;
    const double G1_1_1_0 = det*c0_1_0_1*Jinv_10;
    const double G1_2_0_0 = det*c0_1_0_2*Jinv_00;
    const double G1_2_1_0 = det*c0_1_0_2*Jinv_10;
    const double G1_3_0_0 = det*c0_1_0_3*Jinv_00;
    const double G1_3_1_0 = det*c0_1_0_3*Jinv_10;
