    const double G1_7_1_1 = det*c0_1_0_7*Jinv_11;
    const double G1_8_0_1 = det*c0_1_0_8*Jinv_01;
    const double G1_8_1_1 = det*c0_1_0_8*Jinv_11;
    const double G1_9_0_1 = det*c0_1_0_9*Jinv_01;
    const double G1_9_1_1 = det*c0_1_0_9*Jinv_11;
    const double G1_10_0_1 = det*c0_1_0_10*Jinv_01;
