/// This class defines the interface for the tabulation of the cell
/// tensor corresponding to the local contribution to a form from
/// the integral over a cell.

class Projection_cell_integral_0: public ufc::cell_integral
{
public:

  /// Constructor
  Projection_cell_integral_0() : ufc::cell_integral()
  {
    // Do nothing
  }

  /// Destructor
  virtual ~Projection_cell_integral_0()
  {
    // Do nothing
  }

  /// Tabulate the tensor for the contribution from a local cell
  virtual void tabulate_tensor(double* A,
                               const double * const * w,
                               const ufc::cell& c) const
  {
    // Extract coordinates
    const double * const * x = c.coordinates;
    
    // Compute Jacobian of affine map from reference cell
    const double J_00 = x[1][0] - x[0][0];
    const double J_01 = x[2][0] - x[0][0];
    const double J_10 = x[1][1] - x[0][1];
    const double J_11 = x[2][1] - x[0][1];
      
    // Compute determinant
    double det = J_00*J_11 - J_01*J_10;
      
    // Compute inverse of Jacobian
    // const double Jinv_00 =  J_11 / det;
    // const double Jinv_01 = -J_01 / det;
    // const double Jinv_10 = -J_10 / det;
    // const double Jinv_11 =  J_00 / det;
    
    // Take absolute value of determinant
    det = std::abs(det);
    
    // Compute geometry tensors
    const double G0_ = det;
    
    // Compute element tensor
    A[0] = 8.333333333333318e-02*G0_;
    A[1] = 4.166666666666659e-02*G0_;
    A[2] = 4.166666666666658e-02*G0_;
    A[3] = 4.166666666666659e-02*G0_;
    A[4] = 8.333333333333318e-02*G0_;
    A[5] = 4.166666666666659e-02*G0_;
    A[6] = 4.166666666666658e-02*G0_;
    A[7] = 4.166666666666659e-02*G0_;
    A[8] = 8.333333333333316e-02*G0_;
  }

};

